#!/bin/bash

# Определяем максимальное время выполнения в секундах (1 минута)
max_duration=$((5))

# Определяем интервал между точками в секундах
interval=$((5))

# Вычисляем количество точек, основываясь на интервале и максимальном времени выполнения
total_dots=$((max_duration / interval))

# Цикл с выводом прогресса в виде точек
for ((i=1; i<=total_dots; i++)); do
    echo -n "."
    sleep ${interval}
done

echo  # Переход на новую строку после завершения прогресса
echo "Прогресс завершен!"